import React from 'react';
import { useNavigate } from 'react-router-dom';
import NotFound from '../pages/NotFound';
// import './HelpForm.css'; // Add CSS file for styling

const HelpForm = (props) => {
  let status=JSON.stringify(props.isAuthenticated);
  const [isAuthenticated,setAuthentication] = useState(status);

  useEffect(() => {
      const storedValue = localStorage.getItem('isAuthenticated');
      if (storedValue === false) {
          setAuthentication(storedValue);
      }
  }, []);
    const navigate = useNavigate();

    const actiononsubmit=()=>{
        navigate('/thanks');
        console.log('nav');
    }

  return (
    <>
    {
      isAuthenticated===true ? (
    <div className="flex justify-center items-center h-screen">
    <div className="help-form-container">
      <center>
      <div className="help-line">
        Need help? Contact us at <span className="highlight">support@fatafatyatra.in</span>
      </div>
      </center>
      <center>
      <form className="feedback-form"  onSubmit={actiononsubmit}>
        <input type="text" placeholder="Name" required />
        <input type="email" placeholder="Email" required />
        <textarea placeholder="Message" required></textarea>
        <button type="submit" >Submit</button>
      </form>
      </center>
    </div>
    </div>
  ) : (
    <NotFound />
  )}
  </>
  );
}

export default HelpForm;
