                        <h2 className='text-xl font-bold mb-3'>Who Are You?:</h2>
                        <span className={`inline-block mb-2 lg:mb-0 p-2 rounded mr-1 transition-all font-bold duration-300 ${selectedFare === 'Regular' ? 'bg-cyan-400' : 'bg-gray-200'}`} onClick={() => handleFareSelection('Regular', 5)}>Regular</span>
                        <span className={`inline-block mb-2 lg:mb-0 p-2 rounded mr-1 transition-all font-bold duration-300 ${selectedFare === 'Armed Forces' ? 'bg-cyan-400' : 'bg-gray-200'}`} onClick={() => handleFareSelection('Armed Forces', 10)}>Armed Forces</span>
                        <span className={`inline-block mb-2 lg:mb-0 p-2 rounded mr-1 transition-all font-bold duration-300 ${selectedFare === 'Senior Citizen' ? 'bg-cyan-400' : 'bg-gray-200'}`} onClick={() => handleFareSelection('Senior Citizen', 15)}>Senior Citizen</span>
                        <span className={`inline-block mb-2 lg:mb-0 p-2 rounded mr-1 transition-all font-bold duration-300 ${selectedFare === 'Student' ? 'bg-cyan-400' : 'bg-gray-200'}`} onClick={() => handleFareSelection('Student', 23)}>Student</span>
