import React, { useState, useEffect } from 'react';
import axios from 'axios';
import BottomBar from '../navbar/BottomBar';

const BookingList = () => {
  const [bookings, setBookings] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isError, setIsError] = useState(false);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const response = await axios.get('http://localhost:3001/booked');
        setBookings(response.data);
        setIsLoading(false);
      } catch (error) {
        console.error('Error fetching bookings:', error);
        setIsError(true);
        setIsLoading(false);
      }
    };

    fetchBookings();
  }, []);

  if (isLoading) {
    return (
      <div className="bg-black bg-opacity-80 backdrop-blur-md rounded-lg shadow-md p-6 max-w-2xl w-full mx-auto">
        <h1 className="text-orange-500 text-2xl font-bold mb-4">Loading...</h1>
      </div>
    );
  }

  if (isError || bookings.length === 0) {
    return (
      <div className="bg-black bg-opacity-80 backdrop-blur-md rounded-lg shadow-md p-6 max-w-2xl w-full mx-auto">
        <h1 className="text-red-500 text-2xl font-bold mb-4">
          {isError
            ? 'Error fetching bookings. Please try again later.'
            : 'No bookings found.'}
        </h1>
      </div>
    );
  }

  return (
    <div className="space-y-4">
      {bookings.map((booking) => (
        <div
          key={booking.bookingId}
          className="bg-black bg-opacity-80 backdrop-blur-md rounded-lg shadow-md p-4 flex items-center"
        >
          <div className="flex-grow" class="history">
            <h2 className="text-orange-500 text-lg font-bold mb-2">
              Booking ID: {booking.bookingId}
            </h2>
            <div className="grid grid-cols-2 gap-2">
              <div>
                <h3 className="text-green-500 text-sm font-medium">Pickup:</h3>
                <p className="text-gray-400">{booking.pickupLocation}</p>
                <p className="text-gray-400">{booking.pickupTime}</p>
              </div>
              <div>
                <h3 className="text-red-500 text-sm font-medium">Drop:</h3>
                <p className="text-gray-400">{booking.dropLocation}</p>
                <p className="text-gray-400">{booking.dropTime}</p>
              </div>
              <div>
                <h3 className="text-orange-500 text-sm font-medium">Traveller:</h3>
                <p className="text-gray-400">{booking.travellerName}</p>
              </div>
              <div>
                <h3 className="text-green-500 text-sm font-medium">Seats:</h3>
                <p className="text-gray-400">{booking.seatsBooked}</p>
              </div>
              <div>
                <h3 className="text-red-500 text-sm font-medium">Discount:</h3>
                <p className="text-gray-400">{booking.discount}%</p>
              </div>
              <div>
                <h3 className="text-purple-500 text-sm font-medium">Amount Paid:</h3>
                <p className="text-gray-400">Â© {booking.amountPayed}/-</p>
              </div>
            </div>
          </div>
        </div>
      ))}
      <BottomBar/>
    </div>
  );
};

export default BookingList;
