import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import BottomBar from '../components/navbar/BottomBar';
import img from '/home/geekcoderr/Astra/Projects/TRAVELBACKUP/src/assets/images/garrett-parker-DlkF4-dbCOU-unsplash.jpg';

const LandingPage = () => {
  const [typedText, setTypedText] = useState('');
  const history = useHistory();

  useEffect(() => {
    const texts = [
      "Experience the Best Bus Booking with Fatafat Yatra",
      "Discover the top reasons why Fatafat Yatra is the go-to platform for all your bus travel needs.",
      "Lightning-Fast Booking",
      "Extensive Route Coverage",
      "Unbeatable Prices",
      "Hassle-Free Cancellations",
      "Dedicated Customer Support"
    ];

    let index = 0;
    let currentText = '';
    let isDeleting = false;
    let typingSpeed = 100;

    const type = () => {
      const currentIndex = index % texts.length;
      const fullText = texts[currentIndex];

      if (isDeleting) {
        // Delete text
        currentText = fullText.substring(0, currentText.length - 1);
      } else {
        // Add text
        currentText = fullText.substring(0, currentText.length + 1);
      }

      setTypedText(currentText);

      // Typing speed
      let speed = typingSpeed;
      if (isDeleting) {
        speed /= 2; // Faster delete
      }

      // Text typed completely
      if (!isDeleting && currentText === fullText) {
        speed = 2000; // Pause before deleting
        isDeleting = true;
      } else if (isDeleting && currentText === '') {
        // Text deleted completely
        isDeleting = false;
        index++; // Move to next text
        speed = 500;
      }

      setTimeout(type, speed);
    };

    // Initial call
    type();
  }, []);

  const handleBookNow = () => {
    history.push('/Home'); // Navigate to Home route when booking is clicked
  };

  return (
    <div className="landing-page">
      <div className="hero-section">
        <div className="hero-content">
          <h1>{typedText}</h1>
          <div>
            {typedText === "Experience the Best Bus Booking with Fatafat Yatra" && (
              <p>
                Discover the top reasons why Fatafat Yatra is the go-to platform for
                all your bus travel needs.
              </p>
            )}
            {typedText === "Lightning-Fast Booking" && (
              <p>
                Book your bus tickets in a matter of seconds with our
                user-friendly interface.
              </p>
            )}
            {typedText === "Extensive Route Coverage" && (
              <p>
                Explore a vast network of bus routes, connecting all major
                cities across the country.
              </p>
            )}
            {typedText === "Unbeatable Prices" && (
              <p>
                Get the best deals and discounts on your bus tickets with
                Fatafat Yatra.
              </p>
            )}
            {typedText === "Hassle-Free Cancellations" && (
              <p>
                Easily manage your bookings, including hassle-free
                cancellations and refunds.
              </p>
            )}
            {typedText === "Dedicated Customer Support" && (
              <p>
                Reach out to our friendly customer support team for any
                assistance you may need.
              </p>
            )}
          </div>
          <button className="book-now" onClick={handleBookNow}>
            Book Now
          </button>
        </div>
        <div className="hero-image">
          <img src={img} alt="Fatafat Yatra" />
        </div>
      </div>
      <BottomBar />
    </div>
  );
};

export default LandingPage;
