import React, { useState, useEffect } from 'react';
import axios from 'axios';
import BottomBar from '../components/navbar/BottomBar';
import { useLocation } from 'react-router-dom';

const BookProcessor = (props) => {
  // Destructure bookingData from props
  const { bookingData } = props;
  // Destructure properties from bookingData
  const { from, to, date, fareType } = bookingData;
  const [travellingData, setTravellingData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch data from the API only if booking data is not empty
    if (from && to && date) {
      fetchData(from, to, date);
    } else {
      setLoading(false);
    }
  }, [from, to, date]);

  const fetchData = async (from, to, date) => {
    try {
      console.log('request sent to server');
      const response = await axios.get('http://localhost:3001/travellers', {
        params: { from, to, date },
      });
      console.log('request sent to server');
      setTravellingData(response.data);
      setLoading(false);
    } catch (error) {
      console.log('error server');
      console.error('Error fetching data:', error);
      setLoading(false);
    }
  };

  console.log(travellingData);

  return (
    <>
      <div
        className="flex justify-center items-center"
        style={{ backdropFilter: 'blur(5px)', marginTop: '15%', marginBottom: '20%' }}
      >
        <div className="max-w-6xl w-full px-4 lg:px-0 mx-auto">
          {loading ? (
            <p>Loading...</p>
          ) : travellingData.length === 0 ? (
            <div className="large-card">OOPS! No Travelling available right now!</div>
          ) : (
            <div id="busdata" className="flex flex-col space-y-4">
              {travellingData.map((travel, index) => (
                <div
                  key={index}
                  className="card bg-white bg-opacity-80 rounded-lg shadow-md p-6 w-full max-w-3xl"
                >
                  <div className="flex justify-between items-center">
                    <div>
                      <h2 className="text-yellow-500">Boarding: {from}</h2>
                      <p className="text-gray-500">At: {travellingData[index].pickuptime}</p>
                    </div>
                    <div>
                      <h2 className="text-green-500">Dropping: {to}</h2>
                      <p className="text-gray-500">At: {travellingData[index].droptime}</p>
                    </div>
                  </div>
                  <h3 className="mt-4 text-tomato-500">Bus: {travellingData[index].travels}</h3>
                  <h4 className="mt-2 text-magenta-500">Fare (Per-Seat): {travellingData[index].price}</h4>
                  <h5 className="mt-2 text-gray-500">Available Seats: {travellingData[index].availableSeats}</h5>
                  <div className="flex justify-between items-center mt-4">
                    <div>
                      <label htmlFor={`seats-${index}`} className="text-gray-500">
                        Seats:
                      </label>
                      <select
                        id={`seats-${index}`}
                        className="ml-2 px-2 py-1 border border-gray-300 rounded-md bg-white"
                      >
                        {Array.from(
                          { length: travellingData[index].availableSeats },
                          (_, i) => i + 1
                        ).map((seat) => (
                          <option key={seat} value={seat}>
                            {seat}
                          </option>
                        ))}
                      </select>
                    </div>
                    <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
                      Book
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </>
  );
};

export default BookProcessor;
