import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../assets/App.css'; // Import CSS for styling
import signinasset from '../assets/images/notloggedin.png';

const FeedbackForm = () => {
  const [question, setQuestion] = useState('');
  const [submitted, setSubmitted] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Perform submission logic here, for now just set submitted to true
    setSubmitted(true);
    // Redirect to thanks page
    navigate('/thanks');
  };

  return (
    <div className="feedback-form">
      <h3>Feedback Form</h3>
      <form onSubmit={handleSubmit}>
        <textarea
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          placeholder="Type your feedback here..."
          required
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

const SupportHeader = () => {
  return (
    <div className="support-header">
      <h1>Need Support?</h1>
      <p className="support-info">Call us at: +1-800-123-4567</p>
      <p className="support-info">Email us at: support@example.com</p>
    </div>
  );
};

const FeedbackPage = () => {
  const navigate = useNavigate(); // Importing useNavigate hook

  const handleRegisterClick = () => {
    navigate('/register'); // Navigate to register page
  };

  return (
    <div className="flex justify-center items-center h-screen">
      <div className="max-w-md w-full bg-white p-8 rounded-lg shadow-lg" id="book">
        <h2 className="text-3xl font-semibold text-center mb-6">
          <img src={signinasset} className="w-10 inline mr-1" alt="bus" />
          {' '}
          Sign In
        </h2>
        <p className="text-center mt-4">
          Don't have an account?{' '}
          <Link to="/register">Register here</Link>
        </p>
        <div className="text-center mt-2">
          <button onClick={handleRegisterClick} id="registerButton">
            Register Here!
          </button>
        </div>
      </div>
      <div className="ml-4">
        <SupportHeader />
        <FeedbackForm />
      </div>
    </div>
  );
};

export default FeedbackPage;
