import React from 'react';

const BookingForm = ({ index, fromValue, toValue, date, fareType, errorMessage, handleFromChange, handleToChange, handleDateChange, handleFareTypeChange }) => {
    return (
        <div className='flex flex-col lg:flex-row items-center justify-between mb-10'>
            <fieldset className='flex-1 border border-gray-300 rounded-md p-2 lg:mx-1 lg:mb-0 mb-4'>
                <legend className='font-bold text-lg mb-2'>From</legend>
                <input
                    id={`inputform-${index}`}
                    type="text"
                    placeholder='Where from'
                    value={fromValue}
                    onChange={(e) => handleFromChange(e.target.value)}
                    className={`rounded p-2 w-full font-bold focus:outline-none focus:ring-2 ${errorMessage ? 'focus:ring-red-500' : 'focus:ring-tomato'}`}
                />
                {errorMessage && <div className="text-red-500 font-bold mt-2">{errorMessage}</div>}
            </fieldset>
            <fieldset className='flex-1 border border-gray-300 rounded-md p-2 lg:mx-1 lg:mb-0 mb-4'>
                <legend className='font-bold text-lg mb-2'>To</legend>
                <input
                    id={`inputform-${index}`}
                    type="text"
                    placeholder='to where'
                    value={toValue}
                    onChange={(e) => handleToChange(e.target.value)}
                    className={`rounded p-2 w-full font-bold focus:outline-none focus:ring-2 ${errorMessage ? 'focus:ring-red-500' : 'focus:ring-tomato'}`}
                />
                {errorMessage && <div className="text-red-500 font-bold mt-2">{errorMessage}</div>}
            </fieldset>
            <fieldset className='flex-1 border border-gray-300 rounded-md p-2 lg:mx-1 lg:mb-0 mb-4'>
                <legend className='font-bold text-lg mb-2'>Departure</legend>
                <input
                    id={`inputformdate-${index}`}
                    type="date"
                    value={date}
                    onChange={(e) => handleDateChange(e.target.value)}
                    className={`rounded p-2 w-full font-bold focus:outline-none focus:ring-2 ${errorMessage ? 'focus:ring-red-500' : 'focus:ring-tomato'}`}
                />
            </fieldset>
            <fieldset className='flex-1 border border-gray-300 rounded-md p-2 lg:mx-1 lg:mb-0 mb-4'>
                <legend className='font-bold text-lg mb-2'>Fare Type</legend>
                <select
                    id={`fareType-${index}`}
                    value={fareType}
                    onChange={(e) => handleFareTypeChange(e.target.value)}
                    className={`rounded p-2 w-full font-bold focus:outline-none focus:ring-2 ${errorMessage ? 'focus:ring-red-500' : 'focus:ring-tomato'}`}
                >
                    <option value="">Select Fare Type</option>
                    <option value="Regular">Regular</option>
                    <option value="Armed Forces">Armed Forces</option>
                    <option value="Senior Citizen">Senior Citizen</option>
                    <option value="Student">Student</option>
                </select>
            </fieldset>
        </div>
    );
}

export default BookingForm;
