import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import NotFound from '../pages/NotFound';
import BottomBar from '../components/navbar/BottomBar';

const HelpForm = (props) => {
  const storedValue = localStorage.getItem('isAuthenticated');
  const [isAuthenticated, setAuthentication] = useState(storedValue === 'true');
  const navigate = useNavigate();

  useEffect(() => {
    if (storedValue) {
      setAuthentication(storedValue === 'true');
    }
  }, [storedValue]);

  const handleSubmit = (event) => {
    event.preventDefault(); // Prevent form submission
    if (isAuthenticated) {
      navigate('/thanks');
      console.log('Navigated to thanks');
    } else {
      console.log('User not authenticated');
    }
  };

  const containerStyle = {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
    height: '100vh',
  };

  const formInnerStyle = {
    width: '80%',
    maxWidth: '500px',
    padding: '20px',
    backgroundColor: '#f0f0f0',
    borderRadius: '10px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
  };

  const highlightStyle = {
    color: '#ff6f61',
  };

  const inputStyle = {
    marginBottom: '10px',
    padding: '10px',
    border: '1px solid #ddd',
    borderRadius: '5px',
  };

  const textareaStyle = {
    ...inputStyle,
  };

  const submitBtnStyle = {
    padding: '10px',
    backgroundColor: '#ff6f61',
    color: 'white',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
  };

  const btnHoverStyle = {
    backgroundColor: '#e65245',
  };

  return (
    <>
      {isAuthenticated ? (
        <div style={containerStyle}>
          <div style={formInnerStyle}>
            <div style={{ textAlign: 'center', marginBottom: '20px' }}>
              <p>
                Need help? Contact us at <span style={highlightStyle}>support@fatafatyatra.in</span>
              </p>
            </div>
            <form style={{ display: 'flex', flexDirection: 'column' }} onSubmit={handleSubmit}>
              <input style={inputStyle} type="text" placeholder="Name" required />
              <input style={inputStyle} type="email" placeholder="Email" required />
              <textarea style={textareaStyle} placeholder="Message" required></textarea>
              <button style={submitBtnStyle} type="submit">Submit</button>
            </form>
          </div>
        </div>
      ) : (
        <NotFound />
      )}
      <BottomBar />
    </>
  );
};

export default HelpForm;
