import React, { useState, useEffect } from "react";

const BookingForm = ({ index, trip, fromValue, toValue, dateValue, handleFromChange, handleToChange, handleDateChange }) => {
    const [error, setError] = useState("");

    const validateDate = (selectedDate) => {
        const today = new Date();
        const selected = new Date(selectedDate);

        if (!selectedDate) {
            setError("Date is required.");
            return false;
        } else if (selected < today) {
            setError("Date must be today or later.");
            return false;
        }
        setError("");
        return true;
    };

    const validateLocations = (from, to) => {
        if (from === to) {
            setError("Both locations cannot be the same.");
            return false;
        }
        setError("");
        return true;
    };

    const handleDateBlur = (e) => {
        validateDate(e.target.value);
    };

    const handleFromBlur = (e) => {
        validateLocations(e.target.value, toValue);
    };

    const handleToBlur = (e) => {
        validateLocations(fromValue, e.target.value);
    };

    return (
        <div className='flex flex-col lg:flex-row items-center justify-between mb-10'>
            <fieldset className='flex-1 border border-gray-300 rounded-md p-2 lg:mx-1 lg:mb-0 mb-4'>
                <legend className='font-bold text-lg mb-2'>From</legend>
                <input
                    id="inputform"
                    type="text"
                    placeholder='Where from'
                    value={fromValue}
                    onChange={handleFromChange}
                    onBlur={handleFromBlur}
                    className={`rounded p-2 w-full font-bold focus:outline-none focus:ring-2 focus:ring-tomato ${error && error.includes("locations") ? "border-red-500" : ""}`}
                />
                {error && error.includes("locations") && <p className="text-red-500">{error}</p>}
            </fieldset>
            <fieldset className='flex-1 border border-gray-300 rounded-md p-2 lg:mx-1 lg:mb-0 mb-4'>
                <legend className='font-bold text-lg mb-2'>To</legend>
                <input
                    id="inputform"
                    type="text"
                    placeholder='to where'
                    value={toValue}
                    onChange={handleToChange}
                    onBlur={handleToBlur}
                    className={`rounded p-2 w-full font-bold focus:outline-none focus:ring-2 focus:ring-tomato ${error && error.includes("locations") ? "border-red-500" : ""}`}
                />
            </fieldset>
            <fieldset className='flex-1 border border-gray-300 rounded-md p-2 lg:mx-1 lg:mb-0 mb-4'>
                <legend className='font-bold text-lg mb-2'>Departure</legend>
                <input
                    id="inputformdate"
                    type="date"
                    value={dateValue}
                    onChange={handleDateChange}
                    onBlur={handleDateBlur}
                    min={new Date().toISOString().split("T")[0]}
                    className={`rounded p-2 w-full font-bold focus:outline-none focus:ring-2 focus:ring-tomato ${error && error.includes("Date") ? "border-red-500" : ""}`}
                />
                {error && error.includes("Date") && <p className="text-red-500">{error}</p>}
            </fieldset>
        </div>
    );
};

export default BookingForm;
