// BookProcessor.js

// Booked.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Booked = (props) => {
  const [showMessage, setShowMessage] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowMessage(true);
    }, 3000);

    return () => clearTimeout(timer);
  }, []);

  if (!props.data) {
    return (
      <div
        className="bg-black bg-opacity-80 backdrop-blur-md rounded-lg shadow-md p-6 max-w-2xl w-full"
        style={{ height: '400px' }}
      >
        <h1 className="text-red-500 text-2xl font-bold mb-4">Booking Failed! Try Again!</h1>
        <br />
        <Link
          to="/"
          className="mr-3 border-2 border-red-500 py-2 px-4 rounded-full transition-all duration-300 hover:bg-red-500 hover:text-white"
        >
          <span>Book Again!</span>
        </Link>
      </div>
    );
  }

  console.log(props.data);
  const { id, from, to, pickup, drop, price, travels, seats, discount, customer, payable } = props.data;

  return (
    <div
      className="bg-black bg-opacity-80 backdrop-blur-md rounded-lg shadow-md p-6 max-w-2xl w-full"
      style={{ height: 'auto' }}
    >
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-orange-500 text-2xl font-bold">Success! Booking Confirmed</h2>
      </div>
      <div className="grid grid-cols-2 gap-4">
        <div>
          <h3 className="text-green-500 text-lg font-medium">From:</h3>
          <p className="text-gray-400">{from} Central Parking</p>
        </div>
        <div>
          <h3 className="text-red-500 text-lg font-medium">To:</h3>
          <p className="text-gray-400">{to} Central Parking</p>
        </div>
        <div>
          <h3 className="text-red-500 text-lg font-medium">Pickup Time:</h3>
          <p className="text-gray-400">{new Date(pickup).toLocaleString()}</p>
        </div>
        <div>
          <h3 className="text-purple-500 text-lg font-medium">Drop Time:</h3>
          <p className="text-gray-400">{new Date(drop).toLocaleString()}</p>
        </div>
        <div>
          <h3 className="text-orange-500 text-lg font-medium">Your Traveller:</h3>
          <p className="text-gray-400">{travels}</p>
        </div>
        <div>
          <h3 className="text-green-500 text-lg font-medium">Seats Booked:</h3>
          <p className="text-gray-400">{seats}</p>
        </div>
        <div>
          <h3 className="text-orange-500 text-lg font-medium">Discount:</h3>
          <p className="text-gray-400">{discount}% for {customer}</p>
        </div>
        <div>
          <h3 className="text-red-500 text-lg font-medium">You Have Payed:</h3>
          <p className="text-gray-400">Â© {payable}/-</p>
        </div>
      </div>
      <div className="mt-6 flex justify-left">
        <p className="text-lg font-medium text-purple-500">
          <h3 className="text-red-500 text-lg font-medium">Please Note Your Booking ID:</h3>
          Booking ID: {id}
        </p>
      </div>
      {showMessage && (
        <div className="fixed bottom-4 left-1/2 transform -translate-x-1/2 bg-green-500 text-white px-4 py-2 rounded-md animate-slide-up">
          Thank you for booking! Please book more.
        </div>
      )}
    </div>
  );
};

export default Booked;
