import React, { useState, useEffect } from 'react';
import axios from 'axios';
import BottomBar from '../navbar/BottomBar';

const BookingList = () => {
    const [bookings, setBookings] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:3001/booked')
            .then(response => {
                setBookings(response.data);
            })
            .catch(error => {
                console.error('Error fetching bookings:', error);
            });
    }, []);

    return (
        <>
      {/* <div
        className="flex justify-center items-center" id="history"
        style={{ backdropFilter: 'blur(5px)', marginTop: '15%', marginBottom: '20%' }}
      > */}
        <div className="max-w-6xl w-full h-full px-4 lg:px-0 mx-auto">
                <div className="pt-40 pb-20">
            {bookings.map(booking => (
                <div key={booking._id} className="">
                    <div className="">
                        <div className="font-bold text-xl mb-2">{booking.travellerName}</div>
                        <p className="text-gray-700 mb-2"><span className="font-semibold">Pickup Location:</span> {booking.pickupLocation}</p>
                        <p className="text-gray-700 mb-2"><span className="font-semibold">Drop Location:</span> {booking.dropLocation}</p>
                        <p className="text-gray-700 mb-2"><span className="font-semibold">Pickup Time:</span> {booking.pickupTime}</p>
                        <p className="text-gray-700 mb-2"><span className="font-semibold">Drop Time:</span> {booking.dropTime}</p>
                        <p className="text-gray-700 mb-2"><span className="font-semibold">Discount:</span> {booking.discount}%</p>
                        <p className="text-gray-700 mb-2"><span className="font-semibold">Amount Paid:</span> â‚¹{booking.amountPayed.toFixed(2)}</p>
                        <p className="text-gray-700 mb-2"><span className="font-semibold">Booking ID:</span> {booking.bookingId}</p>
                        <p className="text-gray-700 mb-2"><span className="font-semibold">Seats Booked:</span> {booking.seatsBooked}</p>
                    </div>
                </div>
            ))}
            </div>
            <BottomBar/>
            </div>
        </>
    );
};

export default BookingList;
