import React, { useState, useEffect } from 'react';
import axios from 'axios';

const BookProcessor = () => {
  const [travellingData, setTravellingData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch data from the API
    fetchData();
  }, []);

  const fetchData = async () => {
    const response = await axios.get('your_api_endpoint')
      .then(response => {
        setTravellingData(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
        setLoading(false);
      });
  };

  return (
    // <div className='flex justify-center items-center h-screen' style={{ backdropFilter: 'blur(5px)' }}>
      <div className="max-w-6xl w-full px-4 lg:px-0 mx-auto">
      // Add this JSX to your BookProcessor component

        <div className="container">
          <div className="max-w-6xl w-full px-4 lg:px-0 mx-auto">
            <h1>Travelling Options</h1>
            {loading ? (
              <p>Loading...</p>
            ) : travellingData.length === 0 ? (
              <div className="large-card">
                No Travelling available right now
              </div>
            ) : (
              <div className="column-cards">
                {travellingData.map((travel, index) => (
                  <div key={index} className="card">
                    <h2>{travel.destination}</h2>
                    <p>{travel.description}</p>
                    {/* Add more fields as needed */}
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

      </div>
    // </div>
  );
};

export default BookProcessor;
