import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import BookingForm from "./BookingForm";
import famous from './famous.json';
import BookProcessor from '../../pages/BookProcessor';

const Booking = () => {
    const [buttonText, setButtonText] = useState("Check Availability");
    const [multi, setMulti] = useState([1]);
    const [trip, setTrip] = useState([{ tripType: "", vehicleType: "" }]);
    const [selectedFare, setSelectedFare] = useState("");
    const [regard, setRegard] = useState(5);
    const [bookingDetails, setBookingDetails] = useState({
        from: '',
        to: '',
        date: '',
        faretype: ''
    });
    const [error, setError] = useState('');

    const handleMouseEnter = () => {
        setButtonText("...Let's CheckIt...");
    };

    const handleMouseLeave = () => {
        setButtonText("Check Availability");
    };

    const handleFareSelection = (fareType, cost) => {
        setSelectedFare(fareType);
        setRegard(cost);
        setBookingDetails({...bookingDetails, faretype: fareType});
    };

    const getRandomLocation = () => {
        return famous[Math.floor(Math.random() * famous.length)].location; // Access the 'location' property
    };

    const getUniqueRandomLocations = () => {
        let location1 = getRandomLocation();
        let location2 = getRandomLocation();

        // Ensure no two locations are the same
        while (location2 === location1) {
            location2 = getRandomLocation();
        }

        return [location1, location2];
    };

    const [randomLocation1, randomLocation2] = getUniqueRandomLocations();

    const to = randomLocation1;
    const from = randomLocation2;

    const setFromLocation = (location) => {
        setBookingDetails({...bookingDetails, from: location.toUpperCase()});
    }

    const setToLocation = (location) => {
        setBookingDetails({...bookingDetails, to: location.toUpperCase()});
    }

    const setDate = (date) => {
        setBookingDetails({...bookingDetails, date: date});
    }

    const handleBookingSubmit = (e) => {
        e.preventDefault();
        if (bookingDetails.from === bookingDetails.to) {
            setError('From and To locations cannot be the same');
            return;
        }
        if (!bookingDetails.date) {
            setError('Please select a date');
            return;
        }
        // Further validation if needed
        // If all validations pass, proceed to BookProcessor
    };

    return (
        <div className='pt-40 pb-20'>
            <form className='max-w-5xl px-6 pt-8 pb-10 w-full mx-auto bg-white rounded-lg drop-shadow' id="bookpanel" onSubmit={handleBookingSubmit}>
                <div className='flex items-center justify-between flex-wrap mb-10'>
                    <div className="flex-item">
                        <h1 className="text-4xl font-bold text-center animate-bounce" id="bookid">ðŸŒŸ Just Select & Book  Your Yatra FATAFAT! ðŸŒŸ</h1>
                    </div>
                </div>

                {multi.map((item, index) => 
                    <BookingForm 
                        key={index} 
                        trip={trip} 
                        fromValue={bookingDetails.from} 
                        toValue={bookingDetails.to} 
                        dateValue={bookingDetails.date} 
                        handleFromChange={(e) => setFromLocation(e.target.value)} 
                        handleToChange={(e) => setToLocation(e.target.value)} 
                        handleDateChange={(e) => setDate(e.target.value)} 
                    />
                )}

                {error && <div className="text-red-600">{error}</div>}

                <div className="flex items-center justify-between flex-wrap mb-10">
                    {/* Fare type selection */}
                </div>

                <div className="w-full text-center pt-10">
                    <button type="submit" id="bookButton" onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave} className='py-4 px-12 rounded-md bg-indigo-600 text-white font-bold'>{buttonText}</button>
                </div>
            </form>

            <BookProcessor bookingDetails={bookingDetails} />
        </div>
    );
};

export default Booking;
