import React, { useState, useEffect } from 'react';
import axios from 'axios';
import BottomBar from '../components/navbar/BottomBar';
import { useLocation } from 'react-router-dom';

const BookProcessor = ({ from, to, date, fareType }) => {
  const location = useLocation();
  const { fromr, tor, dater, fareTyper } = location.state;

  console.log(from, to, date, fareType);


  const [travellingData, setTravellingData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch data from the API only if props data is not empty
    if (from && to && date) {
      fetchData(from, to, date);
    } else {
      setLoading(false);
    }
  }, [from, to, date]);

  const fetchData = async (from, to, date) => {
    try {
      console.log('request sent to server');
      const response = await axios.get('/travellers', {
        params: { from, to, date }
      });
      console.log('request sent to server');
      setTravellingData(response.data);
      setLoading(false);
    } catch (error) {
      console.log('error server');
      console.error('Error fetching data:', error);
      setLoading(false);
    }
  };

  return (
    <>
      <div className='flex justify-center items-center h-screen' style={{ backdropFilter: 'blur(5px)' }}>
        <div className="max-w-6xl w-full px-4 lg:px-0 mx-auto">
          {loading ? (
            <p>Loading...</p>
          ) : travellingData.length === 0 ? (
            <div className="large-card">
              OOPS! No Travelling available right now!
            </div>
          ) : (
            <div className="column-cards">
              {travellingData.map((travel, index) => (
                <div key={index} className="card">
                  <h2>{travel.destination}</h2>
                  <p>{travel.description}</p>
                  {/* Add more fields as needed */}
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
      <BottomBar/>
    </>
  );
};

export default BookProcessor;
