import React, { useState, useEffect } from 'react';
import axios from 'axios';
import BottomBar from '../navbar/BottomBar';

const BookingList = () => {
  const [bookings, setBookings] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isError, setIsError] = useState(false);
  const [searchId, setSearchId] = useState('');
  const [searchResult, setSearchResult] = useState(null);
  const [searchError, setSearchError] = useState(false);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const response = await axios.get('http://localhost:3001/booked');
        setBookings(response.data);
        setIsLoading(false);
        setIsError(false);
      } catch (error) {
        console.error('Error fetching bookings:', error);
        setIsError(true);
        setIsLoading(false);
      }
    };

    fetchBookings();
  }, []);

  const handleSearch = async () => {
    if (!searchId) {
      setSearchError(true);
      setSearchResult(null);
      return;
    }

    try {
      const response = await axios.get(`http://localhost:3001/booked/${searchId}`);
      setSearchResult(response.data[0]);
      setSearchError(false);
    } catch (error) {
      console.error('Error fetching booking:', error);
      setSearchResult(null);
      setSearchError(true);
    }
  };

  const renderContent = () => {
    if (isLoading) {
      return (
        <div className="flex justify-center items-center" style={{ height: '100vh', backdropFilter: 'blur(10px)' }}>
          <h1 className="text-red-500 text-2xl font-bold mb-4" id="nobooking-loading">Loading...</h1>
        </div>
      );
    }

    if (isError || bookings.length === 0) {
      const message = isError
        ? 'Error fetching bookings. Please try again later.'
        : 'OOPS! No Previous Bookings found.';

      return (
        <div className="flex justify-center items-center" style={{ height: '100vh', backdropFilter: 'blur(10px)' }}>
          <h1 className="text-red-500 text-2xl font-bold mb-4" id="nobooking">{message}</h1>
        </div>
      );
    }

    return (
      <>
        <h1 className="text-white text-2xl font-bold text-center mb-4" style={{ marginTop: '5%', fontSize: '30px' }}>
          You have {bookings.length} booking{bookings.length !== 1 ? 's' : ''}
        </h1>
        <div className="flex justify-center mb-4">
          <input
            type="text"
            placeholder="Search by Booking ID"
            value={searchId}
            onChange={(e) => setSearchId(e.target.value)}
            className="bg-black bg-opacity-80 backdrop-blur-md rounded-lg shadow-md p-2 text-white w-full max-w-md"
          />
          <button
            onClick={handleSearch}
            className={`bg-orange-500 hover:bg-orange-600 text-white font-bold py-2 px-4 rounded-lg ml-2 ${
              !searchId ? 'opacity-50 cursor-not-allowed' : ''
            }`}
            disabled={!searchId}
          >
            Search
          </button>
        </div>

        {searchError && (
          <div className="flex justify-center items-center" style={{ height: '100vh', backdropFilter: 'blur(10px)' }}>
            <h1 className="text-red-500 text-2xl font-bold mb-4" id="nobooking">Incorrect Booking ID</h1>
          </div>
        )}

        {searchResult && (
          <div className="space-y-8 w-3/4 mx-auto">
            <h1 className="text-white text-2xl font-bold text-center mb-4" style={{ fontSize: '30px' }}>
              Booking Details
            </h1>
            {/* Booking details content */}
          </div>
        )}

        {!searchResult && !searchError && (
          <div className="flex justify-center items-center" style={{ height: '100vh', backdropFilter: 'blur(10px)' }}>
            <h1 className="text-red-500 text-2xl font-bold mb-4" id="nobooking">
              Either booking is not there or invalid ID.
            </h1>
          </div>
        )}
      </>
    );
  };

  return (
    <>
      <div className="flex justify-center items-center" style={{ height: '100vh', backdropFilter: 'blur(10px)' }}>
        <div className="space-y-8 w-3/4" style={{ backdropFilter: 'blur(10px)' }}>
          {renderContent()}
        </div>
      </div>
      <BottomBar />
    </>
  );
};

export default BookingList;
