import { useState } from "react";

const Booking = () => {
    const [trip, setTrip] = useState({
        from: '',
        to: '',
        date: '',
        fareType: ''
    });

    // Function to handle changes in the form inputs
    const handleChange = (e) => {
        const { name, value } = e.target;
        setTrip(prevTrip => ({
            ...prevTrip,
            [name]: value
        }));
    };

    // Function to handle form submission
    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Form submitted:', trip);
        // You can perform further actions like sending data to server here
    };

    return (
        <div className='pt-40 pb-20'>
            <form className='max-w-5xl px-6 pt-8 pb-10 w-full mx-auto bg-white rounded-lg drop-shadow' onSubmit={handleSubmit}>
                <div className='flex items-center justify-between flex-wrap mb-10'>
                    <div className="flex-item">
                        <h1 className="text-4xl font-bold text-center animate-bounce">ðŸŒŸ Just Select & Book Your Yatr! ðŸŒŸ</h1>
                    </div>
                </div>

                <div className="flex items-center justify-between flex-wrap mb-10">
                    <div className="flex-item">
                        <h2 className='text-lg font-bold mb-3'>Starting Location:</h2>
                        <input type="text" name="from" value={trip.from} onChange={handleChange} className="input-field" />
                    </div>
                    <div className="flex-item">
                        <h2 className='text-lg font-bold mb-3'>Ending Location:</h2>
                        <input type="text" name="to" value={trip.to} onChange={handleChange} className="input-field" />
                    </div>
                    <div className="flex-item">
                        <h2 className='text-lg font-bold mb-3'>Date:</h2>
                        <input type="date" name="date" value={trip.date} onChange={handleChange} className="input-field" />
                    </div>
                </div>

                <div className="w-full text-center pt-10">
                    <button type="submit" className='py-4 px-12 rounded-md bg-indigo-600 text-white font-bold'>Check Availability</button>
                </div>
            </form>
        </div>
    )
}

export default Booking;
