import { Link, useNavigate } from "react-router-dom";
import React, { useEffect } from "react";
import Bus from "../../assets/images/bus5.png";
import { useState } from "react";
import loggedinasset from "../../assets/images/loggedin.png";
import notloggedinasset from "../../assets/images/notloggedin.png";
import logofatafat from "../../assets/images/svglogo.svg";
import help from "../../assets/images/help.svg";

const WebNav = (props) => {
  const storedValue = localStorage.getItem("isAuthenticated");
  const [isAuthenticated, setAuthentication] = useState(
    storedValue === "true"
  );
  const navigate = useNavigate(); // Importing useNavigate hook

  useEffect(() => {
    if (storedValue) {
      setAuthentication(storedValue === "true");
    }
  }, []);

  const handleSignOut = () => {
    // Perform sign out actions here, such as clearing session, etc.
    localStorage.setItem("isAuthenticated", JSON.stringify(false));
    setAuthentication(false);
    navigate("/");
  };

  return (
    <div className="webnav_area w-full py-4" id="navb">
      <div className="max-w-6xl w-full px-4 lg:px-0 mx-auto">
        <div className="flex items-center justify-between flex-wrap font-bold">
          <div className="flex-item">
            <Link to="/">
              <h2 className="text-3xl font-extrabold inline">
                <code>
                  <img
                    src={logofatafat}
                    className="w-12 inline mr-1"
                    alt="bus"
                    id="mainlogo"
                  />
                </code>
              </h2>
            </Link>
          </div>

          <div className="flex-item hidden lg:block">
            <Link
              id="asset"
              to="/Home"
              className="mr-3 border-1-custom py-2 px-4 rounded-full transition-all duration-300 hover:bg-gray-300"
            >
              <img
                src={Bus}
                id="assetimage"
                className="w-5 inline mr-1"
                alt="bus"
              />
              <span>Buses</span>
            </Link>
          </div>
          <div className="flex-item hidden lg:block">
            <Link
              id="asset"
              to="/Help"
              className="mr-3 border-1-custom py-2 px-4 rounded-full transition-all duration-300 hover:bg-gray-300"
            >
              <img
                src={help}
                id="assetimage"
                className="w-5 inline mr-1"
                alt="policies"
              />
              <span>Support</span>
            </Link>
          </div>

          {isAuthenticated ? (
            <div className="flex items-center relative">
              <div
                className="cursor-pointer flex items-center"
                onClick={() => {
                  handleMenu();
                }}
              >
                <span className="mr-2">
                  <img
                    src={loggedinasset}
                    className="w-9 inline mr-1"
                    alt="bus"
                  />
                  {' '}
                  Account
                </span>
              </div>
              {/* Rest of the code */}
            </div>
          ) : (
            <div className="flex-item">
              <Link
                to="/sign-in"
                className="border-1-custom py-2 px-4 rounded-md transition-all duration-300 hover:bg-gray-300"
              >
                <img
                  src={notloggedinasset}
                  className="w-5 inline mr-1"
                  alt="bus"
                />
                Sign In or Register
              </Link>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default WebNav;
