import { useState } from "react";
import { Link } from "react-router-dom";
import BookingForm from "./BookingForm";

const Booking = () => {
    const [buttonText, setButtonText] = useState('Check Availability');
    const [selectedFare, setSelectedFare] = useState(null);
    const [regard, setRegard] = useState(null);

    const handleMouseEnter = () => {
        setButtonText('...Let\'s CheckIt...');
    };

    const handleMouseLeave = () => {
        setButtonText('Check Availability');
    };

    const handleFareSelection = (fareType, cost) => {
        setSelectedFare(fareType);
        setRegard(cost);
    };

    return (
        <div className='pt-40 pb-20'>
            <form className='max-w-5xl px-6 pt-8 pb-10 w-full mx-auto bg-white rounded-lg drop-shadow' id="bookpanel">
                <div className='flex items-center justify-between flex-wrap mb-10'>
                    <div className="flex-item">
                        <h1 className="text-4xl font-bold text-center animate-bounce">ðŸŒŸ Just Select & Book  Your Yatr! ðŸŒŸ</h1>
                    </div>
                </div>

                {/* BookingForm component */}

                <div className="flex items-center justify-between flex-wrap mb-10">
                    <div className="flex-item">
                        <h2 className='text-lg font-bold mb-3'>Fare type:</h2>
                        <span className={`inline-block mb-2 lg:mb-0 p-2 rounded mr-1 transition-all duration-300 ${selectedFare === 'Regular' ? 'bg-gray-400' : 'bg-gray-200'}`} onClick={() => handleFareSelection('Regular', 100)}>Regular</span>
                        <span className={`inline-block mb-2 lg:mb-0 p-2 rounded mr-1 transition-all duration-300 ${selectedFare === 'Armed Forces' ? 'bg-gray-400' : 'bg-gray-200'}`} onClick={() => handleFareSelection('Armed Forces', 90)}>Armed Forces</span>
                        <span className={`inline-block mb-2 lg:mb-0 p-2 rounded mr-1 transition-all duration-300 ${selectedFare === 'Senior Citizen' ? 'bg-gray-400' : 'bg-gray-200'}`} onClick={() => handleFareSelection('Senior Citizen', 80)}>Senior Citizen</span>
                        <span className={`inline-block mb-2 lg:mb-0 p-2 rounded mr-1 transition-all duration-300 ${selectedFare === 'Student' ? 'bg-gray-400' : 'bg-gray-200'}`} onClick={() => handleFareSelection('Student', 70)}>Student</span>
                    </div>
                </div>

                <div className="w-full text-center pt-10">
                    <Link to='/bookinglist' id="bookButton" onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave} className='py-4 px-12 rounded-md bg-indigo-600 text-white font-bold'>{buttonText}</Link>
                </div>
            </form>
        </div>
    )
}

export default Booking;
