import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../assets/App.css'; // Import CSS for styling
import signinasset from '../assets/images/notloggedin.png';

const Signin = () => {
  const navigate = useNavigate(); // Importing useNavigate hook
  const [question, setQuestion] = useState('');
  const [submitted, setSubmitted] = useState(false);

  const handleRegisterClick = () => {
    navigate('/register'); // Navigate to register page
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Perform submission logic here, for now just set submitted to true
    setSubmitted(true);
    // Redirect to thanks page
    navigate('/thanks');
  };

  const HelpComponent = () => {
    return (
      <div className="help-info">
        <h3>Need Help?</h3>
        <p>Call us at: +1-800-123-4567</p>
        <p>Email us at: support@example.com</p>
      </div>
    );
  };

  const QuestionForm = () => {
    return (
      <form onSubmit={handleSubmit}>
        <h3>Have a Question?</h3>
        <textarea
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          placeholder="Type your question here..."
          required
        />
        <button type="submit">Submit</button>
      </form>
    );
  };

  return (
    <div className="flex justify-center items-center h-screen">
      <div className="max-w-md w-full bg-white p-8 rounded-lg shadow-lg" id="book">
        <h2 className="text-3xl font-semibold text-center mb-6">
          <img src={signinasset} className="w-10 inline mr-1" alt="bus" />
          {' '}
          Sign In
        </h2>
        <form className="space-y-4">
          <input
            type="text"
            placeholder="Username"
            required
            className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500"
          />
          <input
            type="password"
            placeholder="Password"
            required
            className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500"
          />
          <button
            type="submit"
            className="w-full bg-blue-500 text-white py-2 rounded-lg hover:bg-blue-600 transition duration-300"
          >
            Sign In
          </button>
        </form>
        <p className="text-center mt-4">
          Don't have an account?{' '}
          <Link to="/register">Register here</Link>
        </p>
        <div className="text-center mt-2">
          <button onClick={handleRegisterClick} id="registerButton">
            Register Here!
          </button>
        </div>
      </div>
      <div className="ml-4">
        <HelpComponent />
        <QuestionForm />
      </div>
    </div>
  );
};

export default Signin;
