import { Routes, Route } from "react-router-dom";
import { useState, useEffect } from "react";
import WebNav from "./components/navbar/WebNav";
import BottomBar from "./components/navbar/BottomBar";
import Home from "./pages/Home";
import NotFound from "./pages/NotFound";
import Help from "./pages/Help";
import Landing from "./pages/Landing";
import Thanks from "./pages/Thanks";
import SignIn from "./Authentication/SignIn";
import Register from "./Authentication/Register";
import BookingList from "./components/booking/BookingList";
import NotAvilable from "./pages/NotAvilable";
import "./assets/App.css";

function App() {
  const [authStatus, setAuthStatus] = useState(false);

  useEffect(() => {
    // Retrieve auth status from local storage
    const storedAuthStatus = localStorage.getItem("isAuthenticated");

    // Set auth status based on stored value, default to false if not found
    setAuthStatus(storedAuthStatus === "true");
  }, []);

  return (
    <>
      <WebNav isAuthenticated={authStatus} />
      <Routes>
        <Route path="/" element={<Landing />} />
        <Route
          path="/home"
          element={
            authStatus ? <Home isAuthenticated={authStatus} /> : <NotFound />
          }
        />
        <Route
          path="/Help"
          element={
            authStatus ? <Help isAuthenticated={authStatus} /> : <NotFound />
          }
        />
        <Route
          path="/thanks"
          element={authStatus ? <Thanks /> : <NotFound />}
        />
        <Route
          path="/BookingList"
          element={authStatus ? <BookingList /> : <NotFound />}
        />
        <Route path="/sign-in" element={<SignIn />} />
        <Route path="/register" element={<Register />} />
        <Route path="*" element={<NotAvilable />} />
      </Routes>
    </>
  );
}

export default App;
