import React, { useState, useEffect } from 'react';
import axios from 'axios';
import BottomBar from '../components/navbar/BottomBar';
import { useLocation } from 'react-router-dom';

const BookProcessor = (props) => {
    // Destructure bookingData from props
    const { bookingData } = props;
    // Destructure properties from bookingData
    const { from, to, date, fareType } = bookingData;

    const [travellingData, setTravellingData] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // Fetch data from the API only if booking data is not empty
        if (from && to && date) {
            fetchData(from, to, date);
        } else {
            setLoading(false);
        }
    }, [from, to, date]);

    const fetchData = async (from, to, date) => {
        try {
            console.log('request sent to server');
            const response = await axios.get('http://localhost:3001/travellers', { params: { from, to, date } });
            console.log('request sent to server');
            setTravellingData(response.data);
            setLoading(false);
        } catch (error) {
            console.log('error server');
            console.error('Error fetching data:', error);
            setLoading(false);
        }
    };

    console.log(travellingData);
    return (
        <>
            <div className='flex justify-center items-center' style={{ backdropFilter: 'blur(5px)',marginTop:'15%',marginBottom:'20%'}}>
                <div className="max-w-6xl w-full px-4 lg:px-0 mx-auto">
                    {loading ? (
                        <p>Loading...</p>
                    ) : travellingData.length === 0 ? (
                        <div className="large-card">
                            OOPS! No Travelling available right now!
                        </div>
                    ) : (
                      <div id="busdata">
                      {travellingData.map((travel, index) => (
                        <div key={index} className="w-full md:w-1/3 px-4 mb-8">
                          <div className="card">
                            <h2>Boarding: {from}</h2>
                            <p>At: {travellingData[index].pickuptime}</p>
                            <h2>Dropping: {to}</h2>
                            <p>At: {travellingData[index].droptime}</p>
                            <h3>Bus: {travellingData[index].travels}</h3>
                            <h4>Fare (Per-Seat): {travellingData[index].price} </h4>
                          </div>
                        </div>
                      ))}
                    </div>
                    )}
                </div>
            </div>
        </>
    );
};

export default BookProcessor;
