import React from 'react';
import { useNavigate } from 'react-router-dom';

const BusBookingCard = ({ bus }) => {
  const navigate = useNavigate();

  const handleBookClick = () => {
    navigate('/Home');
  };

  return (
    <div className="bus-booking-card">
      <div className="bus-image-container">
        <img src={bus.image} alt={bus.name} className="bus-image" />
      </div>
      <div className="bus-details">
        <h3 className="bus-name">{bus.name}</h3>
        <p className="bus-description">{bus.description}</p>
        <p className="bus-departure">Departure: {bus.departure}</p>
        <p className="bus-arrival">Arrival: {bus.arrival}</p>
        <p className="bus-price">Price: ${bus.price}</p>
        <button className="book-button" onClick={handleBookClick}>
          <div className="book-button-content">
            <span className="book-text">Book</span>
            <span className="crazy-slogan">Crazy Ride, Unforgettable Journey!</span>
            <span className="animated-slogan">Buckle up for an adventure!</span>
          </div>
        </button>
      </div>
    </div>
  );
};

export default BusBookingCard;
