import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import BottomBar from '../components/navbar/BottomBar';
import { useLocation } from 'react-router-dom';

const BookProcessor = (props) => {
  // Destructure bookingData from props
  const { bookingData } = props;
  // Destructure properties from bookingData
  const { from, to, date, fareType } = bookingData;
  const faretype = fareType;

  const [farePosition, setFarePosition] = useState('');

  useEffect(() => {
    if (fareType === 5) {
      setFarePosition('Regular');
    } else if (fareType === 10) {
      setFarePosition('Armed Forces');
    } else if (fareType === 15) {
      setFarePosition('Senior Citizen');
    } else {
      setFarePosition('Student');
    }
  }, [fareType]);


  const [travellingData, setTravellingData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedSeats, setSelectedSeats] = useState(1);

  const [selectedData, setSelectedData] = useState({});

  useEffect(() => {
    // Fetch data from the API only if booking data is not empty
    if (from && to && date) {
      fetchData(from, to, date);
    } else {
      setLoading(false);
    }
  }, [from, to, date]);

  const fetchData = async (from, to, date) => {
    try {
      console.log('request sent to server');
      const response = await axios.get('http://localhost:3001/travellers', {
        params: { from, to, date },
      });
      console.log('request sent to server');
      setTravellingData(response.data);
      setLoading(false);
    } catch (error) {
      console.log('error server');
      console.error('Error fetching data:', error);
      setLoading(false);
    }
  };

  const handleSeatSelection = (index, seats) => {
    setSelectedSeats(seats);
  };

  const amountElement = useRef(null);
  const fromElement = useRef(null);
  const toElement = useRef(null);
  const atElement = useRef(null);
  const tillElement = useRef(null);
  const busElement = useRef(null);
  const discountElement = useRef(null);
  const seatsElement = useRef(null);
  const payableElement = useRef(null);

  const handleBooking = (travel) => {
    const finalRef = {
      from: fromElement.current?.innerText || from,
      to: toElement.current?.innerText || to,
      at: atElement.current?.innerText || '',
      till: tillElement.current?.innerText || '',
      bus: busElement.current?.innerText || '',
      discount: discountElement.current?.innerText || '',
      seats: seatsElement.current?.value || 0,
      amount: travel.price * selectedSeats,
      payable: payableElement.current?.innerText || 0,
    };
    console.log('Object stored in ref:', finalRef);
  };

  return (
    <>
      <div
        className="flex justify-center items-center"
        style={{ backdropFilter: 'blur(5px)', marginTop: '15%', marginBottom: '20%' }}
      >
        <div className="max-w-6xl w-full px-4 lg:px-0 mx-auto">
          {loading ? (
            <p>Loading...</p>
          ) : travellingData.length === 0 ? (
            <div className="large-card">OOPS! No Travelling available right now!</div>
          ) : (
            <div className="booklist">
              {travellingData.map((travel, index) => (
                <div
                  key={index}
                  className="bg-white bg-opacity-80 rounded-lg shadow-md p-6 h-full flex flex-col justify-between"
                >
                  <div>
                    <div className="flex justify-between items-center">
                      <div>
                        <h2 className="text-orange-500" ref={fromElement}>Boarding: {from}</h2>
                        <p className="text-cyan-500">
                          <b>
                            At:{' '}
                            {new Date(travel.pickuptime).toLocaleString('en-US', {
                              hour12: false,
                              hour: '2-digit',
                              minute: '2-digit',
                              day: '2-digit',
                              month: '2-digit',
                              year: 'numeric',
                            })}
                          </b>
                        </p>
                      </div>
                      <div>
                        <h2 className="text-green-500" ref={toElement}>Dropping: {to}</h2>
                        <p className="text-blue-500">
                          <b>
                            At:{' '}
                            {new Date(travel.droptime).toLocaleString('en-US', {
                              hour12: false,
                              hour: '2-digit',
                              minute: '2-digit',
                              day: '2-digit',
                              month: '2-digit',
                              year: 'numeric',
                            })}
                          </b>
                        </p>
                      </div>
                    </div>
                    <h3 className="mt-4 text-tomato-500" ref={busElement}>Bus: {travel.travels}</h3>
                    <h4 className="mt-2 text-magenta-500">Fare (Per-Seat): © {travel.price} Credits</h4>
                    <h5 className="">Available Seats: {travel.availableSeats}</h5>
                    <h6 className="mt-4 text-tomato-500" ref={discountElement}>Fare Discount: {fareType}% for {farePosition} Customer</h6>
                  </div>
                  <div className="flex justify-between items-center mt-4">
                    <div>
                      <label id="seat" htmlFor={`seats-${index}`} className="text-red-500">
                        Selected Seats:
                      </label>
                      <select
                        ref={seatsElement}
                        id={`seats-${index}`}
                        className="ml-2 px-2 py-1 border border-gray-300 rounded-md bg-white"
                        value={selectedSeats}
                        onChange={(e) => handleSeatSelection(index, Number(e.target.value))}
                      >
                        {Array.from(
                          { length: travel.availableSeats },
                          (_, i) => i + 1
                        ).map((seat) => (
                          <option key={seat} value={seat}>
                            {seat}
                          </option>
                        ))}
                      </select>
                    </div>
                    <div>
                      <p id="payable" className="text-orange-500" ref={payableElement}>
                        PAYABLE © {(selectedSeats * travel.price) - (((selectedSeats * travel.price) * faretype) / 100)}/-
                      </p>
                      <button onClick={()=>handleBooking(travel)} className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
                        Book
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </>
  );
};

export default BookProcessor;
