import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import BookingForm from "./BookingForm";
import famous from './famous.json';
const axios = require('axios').default;

const Booking = () => {
    const [buttonText, setButtonText] = useState('Check Availability');
    const [multi, setMulti] = useState([1]);
    const [trip, setTrip] = useState([{ tripType: "", vehicleType: "" }]);
    const [selectedFare, setSelectedFare] = useState('');
    const [regard, setRegard] = useState(0); // Assuming it's a number, change the initial value accordingly if it's not.

    const handleMouseEnter = () => {
        setButtonText('...Let\'s CheckIt...');
    };

    const handleMouseLeave = () => {
        setButtonText('Check Availability');
    };

    const handleFareSelection = (fareType, cost) => {
        setSelectedFare(fareType);
        setRegard(cost);
    };


    const [locations, setLocations] = useState([]);

    useEffect(() => {
        const fetchLocations = async () => {
            try {
                const response = await fetch('/famous.json');
                const data = await response.json();
                setLocations(data);
            } catch (error) {
                console.error('Error fetching data: ', error);
            }
        };

        fetchLocations();
    }, []);

    const getRandomLocation = () => {
        return locations[Math.floor(Math.random() * locations.length)];
    };

    const getUniqueRandomLocations = () => {
        let location1 = getRandomLocation();
        let location2 = getRandomLocation();

        // Ensure no two locations are the same
        while (location2 === location1) {
            location2 = getRandomLocation();
        }

        return [location1, location2];
    };

    const [randomLocation1, randomLocation2] = getUniqueRandomLocations();


    return (
        <div className='pt-40 pb-20'>
            <form className='max-w-5xl px-6 pt-8 pb-10 w-full mx-auto bg-white rounded-lg drop-shadow' id="bookpanel">
                <div className='flex items-center justify-between flex-wrap mb-10'>
                    <div className="flex-item">
                        <h1 className="text-4xl font-bold text-center animate-bounce">ðŸŒŸ Just Select & Book  Your Yatr! ðŸŒŸ</h1>
                    </div>
                </div>

                {multi.map((item, index) => <BookingForm key={index} trip={trip} />)}

                <div className="flex items-center justify-between flex-wrap mb-10">
                    <div className="flex-item">
                        <h2 className='text-xl font-bold mb-3'>Who Are You?:</h2>
                        <span className={`inline-block mb-2 lg:mb-0 p-2 rounded mr-1 transition-all font-bold duration-300 ${selectedFare === 'Regular' ? 'bg-cyan-400' : 'bg-gray-200'}`} onClick={() => handleFareSelection('Regular', 5)}>Regular</span>
                        <span className={`inline-block mb-2 lg:mb-0 p-2 rounded mr-1 transition-all font-bold duration-300 ${selectedFare === 'Armed Forces' ? 'bg-cyan-400' : 'bg-gray-200'}`} onClick={() => handleFareSelection('Armed Forces', 10)}>Armed Forces</span>
                        <span className={`inline-block mb-2 lg:mb-0 p-2 rounded mr-1 transition-all font-bold duration-300 ${selectedFare === 'Senior Citizen' ? 'bg-cyan-400' : 'bg-gray-200'}`} onClick={() => handleFareSelection('Senior Citizen', 15)}>Senior Citizen</span>
                        <span className={`inline-block mb-2 lg:mb-0 p-2 rounded mr-1 transition-all font-bold duration-300 ${selectedFare === 'Student' ? 'bg-cyan-400' : 'bg-gray-200'}`} onClick={() => handleFareSelection('Student', 23)}>Student</span>
                    </div>
                    <div className="flex-item mt-2 lg:mt-0">
                        <h2 className='text-lg font-bold mb-3'>Most Searched Location:</h2>
                        <span className='inline-block mb-2 lg:mb-0 p-2 bg-gray-200 rounded ml-1 transition-all duration-300 hover:bg-gray-300'>Dhaka to Madaripur</span>
                        <span className='inline-block mb-2 lg:mb-0 p-2 bg-gray-200 rounded ml-1 transition-all duration-300 hover:bg-gray-300'>Dhaka to Savar</span>
                    </div>
                </div>

                <div className="w-full text-center pt-10">
                    <Link to='/bookinglist' id="bookButton" onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave} className='py-4 px-12 rounded-md bg-indigo-600 text-white font-bold'>{buttonText}</Link>
                </div>
            </form>
        </div>
    )
}

export default Booking;
